function cambiarTema(){body.classList.toggle("temaDark"),boton_switch.classList.toggle("active");const t=body.classList.contains("temaDark");localStorage.setItem("tema",t?"dark":"light")}const boton_switch=document.getElementById("switch"),body=document.body;boton_switch.addEventListener("click",cambiarTema),window.addEventListener("load",()=>{const t=localStorage.getItem("tema");"dark"===t&&(body.classList.add("temaDark"),boton_switch.classList.toggle("active"))});